import React, { Component } from "react";
import "./App.css";
import NavBar from "./Components/NavBar";
import UserNavBar from "./Components/UserNavBar"
import UserHome from "./Components/UserHome"
import LogInPage from "./Components/LogInPage"
import SignUp from "./Components/SignUp"

class App extends Component {
  state = {
    cards: [],
    loggedInUser: null,
    showPage: 'logIn'
  };

  componentDidMount() {
    fetch('http://localhost:3000/user', {
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      }
    }).then(res => res.json())
    .then(auth => {
      if (!auth.error) {
        this.setState({
          loggedInUser: auth.user[0],
          showPage: 'user'
        })
      }
    })
  }

  setLoggedInUser = (user) => {
    this.setState({loggedInUser: user, showPage: 'user'})
  }

  handleLogOut = (e) => {
    localStorage.clear()
    this.setState({loggedInUser: null, showPage: 'logIn'})
  }

  handleSignUp = (e) => {
    this.setState()
  }

  render() {
    window.app = this
    if(this.state.showPage === 'logIn'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar />
          <LogInPage setLoggedInUser={this.setLoggedInUser} handleSignUp={this.handleSignUp}/>
        </div>
      )
    }
    if(this.state.showPage === 'signUp'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar />
          <SignUpPage setLoggedInUser={this.setLoggedInUser} />
        </div>
      )
    }
    if(this.state.showPage === 'user'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <UserNavBar user = {this.state.loggedInUser} handleLogOut={this.handleLogOut}/>
          <UserHome />
        </div>
      )
    }
  }
}

export default App;
