import React, { Component } from "react";
import "./App.css";
import NavBar from "./Components/NavBar";
import UserNavBar from "./Components/UserNavBar"
import UserHome from "./Components/UserHome"
import LogIn from "./Components/LogIn"
import SignUp from "./Components/SignUp"
import CreateGame from "./Components/CreateGame"

class App extends Component {
  state = {
    cards: [],
    loggedInUser: null,
    showPage: 'logIn',
    showUser: 'home',

  };

  componentDidMount() {
    fetch('http://localhost:3001/user', {
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      }
    }).then(res => res.json())
    .then(auth => {
      if (!auth.error) {
        if(auth.user){
          this.setState({
            loggedInUser: auth.user,
            showPage: 'user'
          })
        }else{
          this.setState({
            loggedInUser: auth,
            showPage: 'user'
          })
        }
      }
    })
  }

  setLoggedInUser = (user) => {
    this.setState({loggedInUser: user, showPage: 'user'})
  }

  handleLogOut = (e) => {
    localStorage.clear()
    this.setState({loggedInUser: null, showPage: 'logIn'})
  }

  handleSignUp = () => {
    this.setState({showPage: 'signUp'})
  }

  handleUno = () => {
    this.setState({showPage: 'logIn'})
  }

  handleCreateGame = () => {
    this.setState({showUserPage: ''})
  }

  handleCreateSubmit = () => {
    this.setState({createGame: false})
  }

  render() {
    window.app = this
    if(this.state.showPage === 'logIn'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar/>
          <LogIn setLoggedInUser={this.setLoggedInUser} handleSignUp={this.handleSignUp}/>
        </div>
      )
    }
    if(this.state.showPage === 'signUp'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar  handleUno={this.handleUno}/>
          <SignUp setLoggedInUser={this.setLoggedInUser} />
        </div>
      )
    }
    if(this.state.showPage === 'user'){
      if(this.state.showUserPage === 'create')
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <UserNavBar user={this.state.loggedInUser} handleLogOut={this.handleLogOut}/>
          {this.state.createGame ?
            <CreateGame
              user={this.state.loggedInUser}
              handleCreateSubmit={this.handleCreateSubmit}
            />
            : 
            <UserHome
              user={this.state.loggedInUser}
              handleCreateGame={this.handleCreateGame}
            />}
        </div>
      )
    }
  }
}

export default App;
