import React, { Component } from "react";
import "./App.css";
import NavBar from "./Components/NavBar";
import UserNavBar from "./Components/UserNavBar"
import UserHome from "./Components/UserHome"
import LogInPage from "./Components/LogInPage"

class App extends Component {
  state = {
    cards: [],
    loggedInUser: null
  };

  componentDidMount() {
    fetch('http://localhost:3000/user', {
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      }
    }).then(res => res.json())
    .then(auth => {
      if (!auth.error) {
        this.setState({
          loggedInUser: auth.user[0]
        })
      }
    })
  }

  setLoggedInUser = (user) => {
    this.setState({loggedInUser: user})
  }

  handleLogOut = (e) => {
    localStorage.clear()
    this.setState({loggedInUser: null})
  }

  handleSignUp = (e) => {
    this.setState()
  }

  render() {
    window.app = this
    if(this.state.loggedInUser === 'login'){
      return(
        <NavBar />
      )
    }
    return (
      <div style={{background: 'black', height: '700vh'}}>
        {this.state.loggedInUser ? <UserNavBar user = {this.state.loggedInUser} handleLogOut={this.handleLogOut}/> : <NavBar />}
        {this.state.loggedInUser ? <UserHome /> : <LogInPage setLoggedInUser={this.setLoggedInUser} handleSignUp={this.handleSignUp}/>}
      </div>
    );
  }
}

export default App;
