import React, { Component } from "react";

class LogInPage extends Component {
  state = {
    usernameInput: '',
    passwordInput: '',
    errorMessage: ''
  };

  handleUsernameChange = e => {
    this.setState({
      usernameInput: e.target.value
    });
  };

  handlePasswordChange = e => {
    this.setState({
      passwordInput: e.target.value
    });
  };

  handleSubmit = (e) => {
    e.preventDefault()
    fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            username: this.state.nameInputValue,
            password: this.state.passwordInputValue
        })
    })
        .then(res => res.json())
        .then(login => {
            if(login.failed){
                this.setState({ errorMessage: login.message })
            } else {
                localStorage.setItem('token', login.token)
                this.props.setLoggedInUser(login.user)
            }
        })
}

  render() {
    return (
      <form class="ui form">
        <div class="field">
          <label>Username</label>
          <input onChange={this.handleUsernameChange} placeholder="Username" />
        </div>
        <div class="field">
          <label>Password</label>
          <input onChange={this.handlePasswordChange} placeholder="Password" />
        </div>
        <button type="submit" class="ui button" onClick={() => this.props.handleLogIn(e, {username: this.state.username, password: this.state.password})}>
          Log In
        </button>
      </form>
    );
  }
}

export default LogInPage;
