import React from 'react'

export default class ColorSelect extends React.Component{
    state = {
        color: ''
    }

    componentDidMount(){
        this.setState({
            color: (this.props.discard.color ? this.props.discard.color : 'red')
        })
    }

    render(){
        return(
            <div className="ui four item menu">
                <div className='ui red button'>Red</div>
                <div className='ui yellow button'>Yellow</div>
                <div className='ui blue button'>Blue</div>
                <div className='ui green button'>Green</div>
            </div>
        )
    }
}
