import React, { Component } from "react";
import "./App.css";
import NavBar from "./Components/NavBar";
import UserNavBar from "./Components/UserNavBar"
import UserHome from "./Components/UserHome"
import LogIn from "./Components/LogIn"
import SignUp from "./Components/SignUp"

import socketIO from "socket.io-client"

const io = socketIO('http://localhost:3001')

io.on('message', message => console.log(message))

class App extends Component {
  state = {
    cards: [],
    loggedInUser: null,
    showPage: 'logIn'
  };

  componentDidMount() {
    fetch('http://localhost:3001/user', {
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      }
    }).then(res => res.json())
    .then(auth => {
      if (!auth.error) {
        if(auth.user){
          this.setState({
            loggedInUser: auth.user,
            showPage: 'user'
          })
        }else{
          this.setState({
            loggedInUser: auth,
            showPage: 'user'
          })
        }
      }
    })
  }

  setLoggedInUser = (user) => {
    this.setState({loggedInUser: user, showPage: 'user'})
  }

  handleLogOut = (e) => {
    localStorage.clear()
    this.setState({loggedInUser: null, showPage: 'logIn'})
  }

  handleSignUp = () => {
    this.setState({showPage: 'signUp'})
  }

  handleUno = () => {
    this.setState({showPage: 'logIn'})
  }

  render() {
    window.app = this
    if(this.state.showPage === 'logIn'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar/>
          <LogIn setLoggedInUser={this.setLoggedInUser} handleSignUp={this.handleSignUp}/>
        </div>
      )
    }
    if(this.state.showPage === 'signUp'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <NavBar  handleUno={this.handleUno}/>
          <SignUp setLoggedInUser={this.setLoggedInUser} />
        </div>
      )
    }
    if(this.state.showPage === 'user'){
      return(
        <div style={{background: 'black', height: '700vh'}}>
          <UserNavBar user={this.state.loggedInUser} handleLogOut={this.handleLogOut}/>
          <UserHome user={this.state.loggedInUser}/>
        </div>
      )
    }
  }
}

export default App;
