import React from 'react'
import { io } from '../Socket'

export default class Waiting extends React.Component{
    state = {
        game: {},
        player_ids: [],
        players: []
    }

    componentDidMount(){
        io.on('game.joined', (game) => {
            console.log(game)
            this.setState({
                game: game,
                player_ids: JSON.parse(game.users)
            })
            this.state.player_ids.map(player_id => {
                io.emit('player.get', player_id)
            })
            io.on('player', player => this.setState({...this.state.players, player}))
        })
    }

    render(){
        console.log(this.state.game)
        console.log(this.state.players)
        return(
            <div className="ui raised very padded text container segment form">
                <h1>{this.state.game.name}</h1>
                {this.state.players.map(player => {
                    <h2>`$`</h2>
                })}
            </div>
        )
    }
}
